{"changed":true,"filter":false,"title":"proposals_controller.rb","tooltip":"/app/controllers/proposals_controller.rb","value":"class ProposalsController < ApplicationController\n  before_action :set_proposal, only: [:show, :edit, :update, :destroy]\n\n  # GET /proposals\n  # GET /proposals.json\n  def index\n    @proposals = Proposal.all\n  end\n\n  # GET /proposals/1\n  # GET /proposals/1.json\n  def show\n  end\n\n  # GET /proposals/new\n  def new\n    @proposal = Proposal.new\n  end\n\n  # GET /proposals/1/edit\n  def edit\n  end\n\n  # POST /proposals\n  # POST /proposals.json\n  def create\n    @proposal = Proposal.new(proposal_params)\n\n    respond_to do |format|\n      if @proposal.save\n        format.html { redirect_to @proposal, notice: 'Proposal was successfully created.' }\n        format.json { render :show, status: :created, location: @proposal }\n      else\n        format.html { render :new }\n        format.json { render json: @proposal.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n  \n  def creador\n    @proposal = Proposal.create(:title => params[:title], :user_id => 1, :object => params[:object], :finality => params[:finality], :subject_id => params[:subject_id], :scope_id => params[:scope_id])\n    render :json => @proposal\n  end\n\n  # PATCH/PUT /proposals/1\n  # PATCH/PUT /proposals/1.json\n  def update\n    respond_to do |format|\n      if @proposal.update(proposal_params)\n        format.html { redirect_to @proposal, notice: 'Proposal was successfully updated.' }\n        format.json { render :show, status: :ok, location: @proposal }\n      else\n        format.html { render :edit }\n        format.json { render json: @proposal.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # DELETE /proposals/1\n  # DELETE /proposals/1.json\n  def destroy\n    @proposal.destroy\n    respond_to do |format|\n      format.html { redirect_to proposals_url, notice: 'Proposal was successfully destroyed.' }\n      format.json { head :no_content }\n    end\n  end\n\n  private\n    # Use callbacks to share common setup or constraints between actions.\n    def set_proposal\n      @proposal = Proposal.find(params[:id])\n    end\n\n    # Never trust parameters from the scary internet, only allow the white list through.\n    def proposal_params\n      params.require(:proposal).permit(:user_id, :title, :object, :finality, :scope_id, :subject_id)\n    end\nend\n","undoManager":{"mark":99,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":40,"column":94},"end":{"row":40,"column":95},"action":"insert","lines":["j"]}]}],[{"group":"doc","deltas":[{"start":{"row":40,"column":95},"end":{"row":40,"column":96},"action":"insert","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":40,"column":96},"end":{"row":40,"column":97},"action":"insert","lines":["c"]}]}],[{"group":"doc","deltas":[{"start":{"row":40,"column":97},"end":{"row":40,"column":98},"action":"insert","lines":["t"]}]}],[{"group":"doc","deltas":[{"start":{"row":40,"column":99},"end":{"row":40,"column":127},"action":"insert","lines":[", :object => params[:object]"]}]}],[{"group":"doc","deltas":[{"start":{"row":40,"column":102},"end":{"row":40,"column":108},"action":"remove","lines":["object"]}]}],[{"group":"doc","deltas":[{"start":{"row":40,"column":102},"end":{"row":40,"column":103},"action":"insert","lines":["f"]}]}],[{"group":"doc","deltas":[{"start":{"row":40,"column":103},"end":{"row":40,"column":104},"action":"insert","lines":["i"]}]}],[{"group":"doc","deltas":[{"start":{"row":40,"column":102},"end":{"row":40,"column":104},"action":"remove","lines":["fi"]},{"start":{"row":40,"column":102},"end":{"row":40,"column":110},"action":"insert","lines":["finality"]}]}],[{"group":"doc","deltas":[{"start":{"row":40,"column":127},"end":{"row":40,"column":128},"action":"remove","lines":["t"]}]}],[{"group":"doc","deltas":[{"start":{"row":40,"column":126},"end":{"row":40,"column":127},"action":"remove","lines":["c"]}]}],